require_once 'Customweb/Payment/Authorization/AbstractPaymentMethodWrapper.php';


/**
 * 
 * @author Nico Eigenmann
 * @Method(paymentMethods={'PayPal', 'PayPalPlus'})
 */
class Customweb_PayPalPlus_Method_PayPal extends Customweb_Payment_Authorization_AbstractPaymentMethodWrapper {
	
	/**
	 * This map contains all supported payment methods.
	 *       					    			 	 
	 * @var array
	 */
	protected static $paymentMapping = array(
		'paypal' => array(
			'machine_name' => 'PayPal',
 			'method_name' => 'PayPal',
 			'parameters' => array(
			),
 			'not_supported_features' => array(
				0 => 'AjaxAuthorization',
 			),
 			'image_color' => 'data:image/png;base64,',
 			'image_grey' => 'data:image/png;base64,',
 		),
 		'paypalplus' => array(
			'machine_name' => 'PayPalPlus',
 			'method_name' => 'PayPal Plus',
 			'parameters' => array(
			),
 			'not_supported_features' => array(
				0 => 'PaymentPage',
 			),
 			'image_color' => 'data:image/png;base64,',
 			'image_grey' => 'data:image/png;base64,',
 		),
 	);
	
	
	/**
	 *
	 * @var Customweb_DependencyInjection_IContainer
	 */
	private $container = null;
	
	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod) {
		parent::__construct($paymentMethod);
	}
	

	protected function getPaymentInformationMap() {
		return self::$paymentMapping;
	}
	
	public function isDeferredCapturingSupported(){
		return $this->existsPaymentMethodConfigurationValue('capturing');
	}

}