  function inputInt(inputs) {
    var showClass = "show";
    inputs.each(function() {
      $(this)
        .on("checkval", function() {
          var label = $(this)
            .parent()
            .find("label");

          if (this.value !== "") {
            label.addClass(showClass);
            $(this).addClass(showClass);
          } else {
            label.removeClass(showClass);
            $(this).removeClass(showClass);
          }
        })
        .on("keyup", function() {
          $(this).trigger("checkval");
        });
    });
  }

